function timetable_adjust_time($bit_type, $time){
    
    switch ($bit_type){
    
        case 64:
            
            //SERVER SETTING 64 BIT INT
            $time = strtotime($time)*1000;
            $days = 2 * 86400000;
            $time = $time + $days;
            break;
    
        case 32:
            //LOCALHOST SETTING 32 BIT INT
            $time = strtotime($time);
            $days = 2 * 86400;
            $time = $time + $days;
            break;
    }
    
    return $time;
}
